,ifmmAdminCd
,ifmmRegistTypeCd
,ifmmName
,ifmmDelNy
,ifmmId
,ifmmPassword
,ifmmGenderCd
,ifmmBirthday
,ifmmRecommend
,ifmmEmailConsentNy
,ifmmPushConsentNy
,ifmmPersonerinfoCd
,regDateTime
,modDateTime
) VALUES (
#{seq}
,2
,#{ifmmRegistTypeCd}
,#{ifmmName}
,0
,#{ifmmId}
,#{ifmmPassword}
,#{ifmmGenderCd}
,#{ifmmBirthday}
,#{ifmmRecommend}
,#{ifmmEmailConsentNy}
,#{ifmmPushConsentNy}
,#{ifmmPersonerinfoCd}
,#{regDateTime}
,#{modDateTime}
);

SELECT last_insert_id()

mapper.update:
UPDATE
		infrMember
	SET 
		ifmmName = #{ifmmName}
		,ifmmId = #{ifmmId}
		,ifmmPassword = #{ifmmPassword}
		,ifmmGenderCd = #{ifmmGenderCd}
		,ifmmBirthday = #{ifmmBirthday}
		,ifmmRecommend = #{ifmmRecommend}
		,ifmeEmailAccount = #{ifmeEmailAccount}
		,ifmeEmailDomainCd = #{ifmeEmailDomainCd}
		,ifmmEmailConsentNy = #{ifmmEmailConsentNy}
		,ifmmPushConsentNy = #{ifmmPushConsentNy}
		,ifmmPersonerinfoCd = #{ifmmPersonerinfoCd}
	WHERE 1=1
		AND seq = #{seq}

UPDATE
		infrMember
	SET 
		ifmmName = #{ifmmName}
		,ifmmId = #{ifmmId}
		,ifmmPassword = #{ifmmPassword}
		,ifmmGenderCd = #{ifmmGenderCd}
		,ifmmBirthday = #{ifmmBirthday}
		,ifmmRecommend = #{ifmmRecommend}
		,ifmmEmailConsentNy = #{ifmmEmailConsentNy}
		,ifmmPushConsentNy = #{ifmmPushConsentNy}
		,ifmmPersonerinfoCd = #{ifmmPersonerinfoCd}
	WHERE 1=1
		AND seq = #{seq}

UPDATE infrMember SET ifmmDelNy = 1
	WHERE 1=1
		AND seq = #{seq}

mapper.delete:
DELETE FROM infrMember
	WHERE 1=1
		AND seq = #{seq}
