mapper.insert:
INSERT INTO infrMember (
seq
,ifmmName
,ifmmDelNy
,ifmmId
,ifmmPassword
,ifmmGenderCd
,ifmmBirthday
,ifmmRecommendSeq
,regDateTime
,modDateTime
) VALUES (
#{seq}
,#{ifmmName}
,0
,#{ifmmId}
,#{ifmmPassword}
,#{ifmmGenderCd}
,#{ifmmBirthday}
,#{ifmmRecommendSeq}
,#{regDateTime}
,#{modDateTime}
)

SELECT last_insert_id()

mapper.update:
UPDATE
		infrMember
	SET 
		ifmmName = #{ifmmName}
		,ifmmId = #{ifmmId}
		,ifmmPassword = #{ifmmPassword}
		,ifmmBirthday = #{ifmmBirthday}
		,ifmmRecommendSeq = #{ifmmRecommendSeq}
	WHERE 1=1
		AND seq = #{seq}

UPDATE infrMember SET ifmmDelNy = 1
	WHERE 1=1
		AND seq = #{seq}

mapper.delete:
DELETE FROM infrMember
	WHERE 1=1
		AND seq = #{seq}
