mapper.insert:
INSERT INTO StoreInfo (
stifSeq
,stifName
,stifDelNy
,stifDesc
,stifAddress1
,stifAddress2
,stifOC
,stifFoodTypeCd
,regDateTime
,infrseq
) VALUES (
#{stifSeq}
,#{stifName}
,0
,#{stifDesc}
,#{stifAddress1}
,#{stifAddress2}
,#{stifOC}
,#{stifFoodTypeCd}
,#{regDateTime}
,#{infrseq}
);


SELECT last_insert_id()

mapper.update:
UPDATE
		storeinfo
	SET 
		stifName = #{stifName}
		,stifDesc = #{stifDesc}
		,stifAddress1 = #{stifAddress1}
		,stifAddress2 = #{stifAddress2}
		,stifOC = #{stifOC}
		,stifFoodTypeCd = #{stifFoodTypeCd}
	WHERE 1=1
		AND stifSeq = #{infrseq}

UPDATE storeinfo SET stifDelNy = 1
	WHERE 1=1
		AND stifSeq = #{stifSeq}
